using System;
using System.Collections;
using System.Collections.Generic;
using UnityEngine;
using UnityEngine.UI;
using WebP;

public class ReadWebPBase64Image : MonoBehaviour
{
    [SerializeField]
    private Image m_img;
    void Start()
    {
        LoadBase64WebPImage("");
    }
    private void LoadBase64WebPImage(string base64Str)
    {
        // 1. 将Base64字符串转换为字节数组
        byte[] byteArray = Convert.FromBase64String(base64Str);
        // 2. 获取WebP图片的宽和高
        Texture2DExt.GetWebPDimensions(byteArray, out int width, out int height);
        // 3. 创建一个Texture2D并加载WebP数据
        Texture2D texture = Texture2DExt.CreateWebpTexture2D(width, height, isUseMipmap: true, isLinear: false);
        //这段代码将 byteArray 中的 WebP 图片数据加载到 texture 中，并设置生成多级渐进纹理（mipmaps）、使用线性颜色空间，并使用 10 MB 的缓冲区和最大像素限制为 4915200，以提高解码效率和控制资源使用。
        Texture2DExt.LoadTexture2DFromWebP(byteArray, texture, lMipmaps: true, lLinear: true, new byte[1024 * 1024 * 10], 4915200);
        // 4. 将Texture2D转换为Sprite
        Sprite sprite = Sprite.Create(texture, new Rect(0, 0, texture.width, texture.height), new Vector2(0.5f, 0.5f));
        // 5. 设置m_img的Sprite
        m_img.sprite = sprite;
    }
}
